import styled from 'styled-components'

export const ThemesContainer = styled.div`
  max-width: 1800px;
  height: 800px;
  padding: 0 30px; 
  position: relative;
  z-index: 1;
  display: flex;
  justify-content: center;
  background: #0c0c0c;
`;

/*export const descP = styled.p`
  color: #5A6B50;
  font-size: 14px;
`; */
export const ThemeBg = styled.div`
  position: absolute;
  top: 0;
  right 0;
  bottom: 0;
  left: 0;
  width: 100%;
  height: 100%;
  overflow: hidden;
`

export const VideoBg = styled.video`
  width: 100%;
  height: 100%;
  -o-object-fit: cover;
  object-fit: cover;
`

export const TextUl = styled.ul`
  color: #F4FDE6;
  font-size: 24px;
  position: relative;
  padding: 0 30px 0 30px;
  margin-top: 30px;
  margin-botom: 50px; 

`

export const TextLi = styled.li`
    font-size: 24px;
    margin-left: 40px;
`

export const ThemeSection = styled.div`
  z-index: 3;
  max-width: 1800px;
  position: absolute;
`;

export const MineEnergyContainer = styled.div`
    display: flex;
    flex-direction: vertical;
    justify-content: center;
    align-content: center;
    gap: 30px;
`

export const EnergySection = styled.div`
    width: 300px;
    height: 300px;
    background: #FFB502;
    border-radius: 0 30px 0 0;
    box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.5), 0 6px 20px 0 rgba(0, 0, 0, 0.19);
`;

export const MineSection = styled.div`
    width: 300px;
    height: 300px;
    background: #00AADE;
    border-radius: 0 30px 0 0;
    box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19);
`;
