import styled from 'styled-components'
import { Link as LinkRouter } from 'react-router-dom'
import { Link as LinkScroll } from 'react-scroll'

export const Nav = styled.nav`
  background: ${({ scrollNav}) => (scrollNav ? 'rgb(200,193,183)' : '#dde0e5')} ;
  height: 80px;
  margin-top: -80px
  display: flex;
  justify-content: center;
  align-items: center;
  font-size: 1.2rem;
  position: sticky;
  top: 0;

  z-index: 10;

  @media screen and (max-width: 960px) {
    transition: 0.8s all ease;
  }
`

export const NavbarContainer = styled.div`
  display: flex;
  justify-content: space-between;
  height: 80px;
  z-index: 1;
  width: 100%;
  padding: 0 24px;
  max-width: 1500px;
`

export const NavLogo = styled(LinkRouter)`
  justify -self: flex-start;
  cursor: pointer;
  font-size: 1.5rem;
  display: flex;
  align-items: center;
  margin-left: 24px;
  font-weight: bold;
  text-decoration: none;
`;


export const Burger = styled.div`
  display: none;

  @media screen and (max-width: 768px) {
    display: block;
    position: absolute;
    top: 0;
    right: 0;
    transform: translate(-100%, 60%);
    font-size: 1.8rem;
    cursor: PointerEvent;
  }
`

export const NavMenu = styled.ul`
display: flex;
align-items: center;
list-style: none;
text-align-center;

@media screen and (max-width: 768px){
  display: none;
}
`

export const NavItem = styled.li`
  height: 40px;
  border: 1px solid #BDD3DB;

  &.hover{
    background: green;
  } 

`

export const NavLinks = styled(LinkScroll)`
  color: #395E70;
  display: flex;
  align-items: center;
  text-decoration: none;
  padding: 0 1rem;
  height: 100%;
  cursor: pointer;

  &.active {
    border-bottom: 3px solid #97A923;
  }
`;
