import styled from 'styled-components'

export const ThemesContainer = styled.div`
  max-width: 1800px;
  height: 800px;
  padding: 0 30px; 
  position: relative;
  z-index: 1;
  display: flex;
  justify-content: center;
  background: #0c0c0c;
`;

/*export const descP = styled.p`
  color: #5A6B50;
  font-size: 14px;
`; */
export const ThemeBg = styled.div`
  position: absolute;
  top: 0;
  right 0;
  bottom: 0;
  left: 0;
  width: 100%;
  height: 100%;
  overflow: hidden;
`

export const VideoBg = styled.video`
  width: 100%;
  height: 100%;
  -o-object-fit: cover;
  object-fit: cover;
`

export const ThemeSection = styled.div`
  z-index: 3;
  max-width: 1800px;
  position: absolute;
  display: flex;
  flex-direction: vertical;
  justify-content: center;
  align-content: center;
  gap: 30px;
`;

export const EnergySection = styled.div`
  width: 150px;
  height: 150px;
  background: yellow;
`;

export const MineSection = styled.div`
width: 150px;
height: 150px;
background: blue;
`;
