import React from 'react';
import Video from '../../videos/resM.mp4';
import energy from '../../images/energy.jpg';
import mine from '../../images/mine.jpg';
import { EnergySection, MineEnergyContainer, MineSection, ThemeBg, TextUl, TextLi, ThemesContainer, ThemeSection, VideoBg } from './ThemeElements';

function Themes() {
    return (
        <ThemesContainer>
            <ThemeBg>
                <VideoBg autoPlay loop muted src= {Video} type='map/mp4' />
            </ThemeBg>

            <ThemeSection>
                <TextUl>Resource Matters est un organisme non gouvernemental qui a pour but de:
                    <TextLi>
                        Lutter pour la bonne gestion des ressources miniers et la transparence des 
                        redevances payées par les entreprises exploitrices aux collectivités locales.
                    </TextLi>
                    <TextLi>
                        Oeuvrer pour l'électrification à faible coût et la plus adaptée dans toute la RDC 
                        d'ici 2030, et mettre à la disposition de la population congolaise, un outil
                        de modélisation et de scénarisation pour l'électrification d'une zone donnée.
                    </TextLi>
                  </TextUl>
                <MineEnergyContainer>
                    <EnergySection><img src={energy} alt="" width="320px" height="320px" /></EnergySection>
                    <MineSection><img src={mine} alt="" width="320px" height="320px" /></MineSection>
                </MineEnergyContainer>
            </ThemeSection>
        </ThemesContainer>
    );
};

export default Themes
